use yew::prelude::*;

#[function_component(Logger)]
fn logger() -> Html {
    let count = use_state(|| 0);
    {
        let count = count.clone();
        use_effect_with(count.clone(), move |count| {
            log::info!("Count changed: {}", **count);
            || ()
        });
    }
    html! { <div>{*count}</div> }
}
