use std::ptr;

unsafe fn swap_raw<T>(a: *mut T, b: *mut T) {
    unsafe {
        let tmp = ptr::read(a);
        ptr::write(a, ptr::read(b));
        ptr::write(b, tmp);
    }
}

fn main() {
    let mut x = 1;
    let mut y = 2;
    unsafe { swap_raw(&mut x, &mut y) };
    println!("x={}, y={}", x, y);
}
